mapper.select:
		SELECT
		t.id, t.title AS title,t.type AS type,t.content AS content,t.create_time AS createTime,t.edit_time AS editTime,t.table_name AS tableName,t.auditing AS auditing
		FROM industry_data t where true
			
				and t.title like  concat('%',#{title},'%') and t.auditing = 1
			
            
                and t.table_name = #{tableName}
            
            
                 and t.create_time >= #{startTime} 
            
            
                 and t.create_time <= #{endTime}
